// Fun√ß√£o simples para gerar string hexadecimal aleat√≥ria
function generateRandomHex(length = 20) {
  const characters = '0123456789ABCDEF';
  let result = '';

  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * characters.length);
    result += characters[randomIndex];
  }

  return result;
}

// Gera a string aleat√≥ria (20 caracteres como no exemplo)
const randomHex = generateRandomHex(20);

return {randomHex};

const { createClient } = require('redis');

// O Error Trigger nos d√° o ID da execu√ß√£o que falhou
const executionId = $('Error Trigger').item.json.execution.id;
if (!executionId) {
  return [{ json: { action: 'NO_EXECUTION_ID' } }];
}

const client = createClient({
  socket: { host: 'redis', port: 6379 },
  database: 10
});

let output = {};

try {
  await client.connect();

  // 1. Construir e acessar a fila de processamento √∫nica
  const processingQueueKey = `processing:exec:${executionId}`;
  const contextKey = `context:exec:${executionId}`;
  // Buscar as duas informa√ß√µes em paralelo para mais efici√™ncia
  const [failedPayloadString, contextString] = await Promise.all([
    client.lPop(processingQueueKey), //"Abrimos" o payload para extrair seus dados necess√°rios da inst√¢ncia pra gerar a mensagem de erro.
    client.get(contextKey) //Aqui guardamos o token separadinho (j√° que s√≥ o conseguimos no meio do flow), pra montar o payload necess√°rio (l√° buscamos os dados do chatwoot com base no token, √© claro).
  ]);

  if (!failedPayloadString) {
    return [{ json: { action: 'EMPITY_PAYLOAD' } }]; //Por algum motivo esse flow foi chamado com a fila vazia (observei quando o primeiro code do MAIN Chatwoot Receptor falha :p)
  }

  // Se o contexto n√£o for encontrado
  const context = contextString ? JSON.parse(contextString) : null;
  if (!context) {
    throw new Error("Context not found - cannot proceed without instanceToken");
  }

  // 2. Extrair informa√ß√µes cruciais do payload para construir as outras chaves
  const payload = JSON.parse(failedPayloadString);
  // Extrarir dados do payload para formar as outras chaves √∫nicas
  const accountId = payload.extra.accountId;
  const inboxId = payload.extra.contac_inbox.inbox_id;
  const senderId = payload.toSend.body.conversation.meta.sender.identifier.split("@")[0];
  const messageId = String(payload.extra.messageId);

  //Formar chaves √∫nicas
  //const queueKey = `cw_to_uz_queue:acc${accountId}:i${inboxId}:${senderId}`;
  //const lockKey = `lock:cw_to_uz_process:acc${accountId}:i${inboxId}:${senderId}`;
  // üëÜüèº J√Å ADICINEI COMO BASE NO PAYLOA NO ROUTER!!!
  const queueKey = payload.queueKey;
  const retriesKey = `retries:acc${accountId}:i${inboxId}:${senderId}`;
  //const dlqKey = `dlq:acc${accountId}:i${inboxId}:${senderId}`;

  // *** UPGRADE: VERIFICAR O TAMANHO DA FILA ANTES DE QUALQUER A√á√ÉO ***
  const remainingCount = await client.lLen(queueKey);

  // 3. Incrementar e obter o contador de tentativas
  const attempt = await client.hIncrBy(retriesKey, messageId, 1);

  // *** A SUA SUGEST√ÉO EM A√á√ÉO ***
  // Juntando o userToken ao payload com o spread operator.
  const finalPayload = { ...context, ...payload };

  // Prepara os dados de sa√≠da
  output = { 
    action: '', 
    payload: finalPayload, 
    attempt: attempt
    //queueKey: queueKey, J√Å COMO BASE NO PAYLOAD!!! 
    //lockKey: lockKey
  };

  // 4. Tomar a decis√£o
  if (attempt < 3) { // 2¬™ ou 3¬™ tentativa (a 1¬™ j√° foi a primeira execu√ß√£o do MAIN receptor)
    output.action = 'RETRY';

    // *** UPGRADE: MENSAGEM DIN√ÇMICA PARA RETRY ***
    if (remainingCount === 1) {
      output.message = `üö® Este envio falhou (a mensagem seguinte est√° na fila). Faremos a tentativa ${attempt + 1} (de 3) em 10 segundos. Para cancelar e limpar a fila digite \`..limpar\`.`;
    } else if (remainingCount > 1) {
      output.message = `üö® Este envio falhou (as pr√≥ximas ${remainingCount} mensagens est√£o na fila). Faremos a tentativa ${attempt + 1} (de 3) em 10 segundos. Para cancelar e limpar a fila digite \`..limpar\`.`;
    } else {
      output.message = `üö® Este envio falhou, faremos a tentativa ${attempt + 1} (de 3) em 10 segundos`;
    }

    // *** UPGRADE FINAL: Adicionar o n√∫mero da tentativa ao payload antes de reenfileirar ***
    payload.attempt = attempt; // Adicionamos a propriedade 'attempt' ao objeto payload
    const payloadStringToRequeue = JSON.stringify(payload); // Convertemos o objeto MODIFICADO de volta para string

    await client.lPush(queueKey, payloadStringToRequeue); // üëàüèºüëàüèºüëàüèº Devolve a MENSAGEM MODIFICADA para a FRENTE da fila principal!

  } else { // 4¬™ vez aqui j√° √© falha permanente
    output.action = 'FAIL'; //Nem usamos isso no Switch, porque seguimos igual. S√≥ vamos mudar a mensagem. Mas engatilharemos os mesmos flows. A diferen√ßa √© que n√£o reenfileiramos a mensagem.

    // *** UPGRADE: MENSAGEM DIN√ÇMICA PARA FAIL ***
    if (remainingCount === 1) {
      output.message = `A terceira tentativa tamb√©m falhou. A mensagem n√£o foi enviada! Tentaremos enviar sua pr√≥xima mensagem em 10s, para cancelar digite \`..limpar\`.`;
    } else if (remainingCount > 1) {
      output.message = `A terceira tentativa tamb√©m falhou. A mensagem n√£o foi enviada! Tentaremos enviar sua pr√≥xima mensagem em 10s, para cancelar e limpar a fila (${remainingCount} mensagens) digite \`..limpar\`.`;
    } else {
      output.message = `A terceira tentativa tamb√©m falhou. Mensagem n√£o enviada!`;
    }

    // Move para a Dead-Letter Queue
    //await client.lPush(dlqKey, failedPayloadString); N√£o precisamos de dlq! Bobagem visto que √© coisa do agente.
    // Limpa o contador para n√£o interferir em futuras mensagens iguais ??? N√£o faz sentido. Acho que √© s√≥ limpeza.
    await client.hDel(retriesKey, messageId);
  }

  await client.del(contextKey); // Chave ef√™mera da execu√ß√£o passada. Vamos reengatilhar e pegar novamente.
  // A processingQueueKey (tamb√©m ef√™mera) j√° foi esvaziada com o LPOP no comecinho! 

} catch (error) {
  throw error;
} finally {
  if (client.isOpen) {
    await client.quit();
  }
}

return [{ json: output }];

