// O baserow data pode ser 1. o pr√≥prio payload do webhook enviado pelo Baserow, ou 2. o fruto da contulta (switch fallback)!

// Passar os dados do webhook pra c√° pode at√© parecer redundante,
// mas estamos padronizando o payload para o pr√≥ximo node!

const instanceLine = $('Workflow Trigger').item.json.body.items[0];

let multipleRows = null;
if ($('Get InstanceSS Rows').isExecuted) {
  multipleRows = $('Get InstanceSS Rows').item.json || $json;
}



// N√£o posso assumir que porque webhookk tem items (ele ter√° nos dois casos, a quest√£o √© edi√ß√£o em uma outra linha n√£o vai ter determinados campos). Precisamos verificar se webhookData tem a estrutura esperada (pelo menos uma das propriedades da tabela de inst√¢ncias)
const sourceData = (instanceLine && instanceLine["Instance Name"]) ? instanceLine : multipleRows;


// Normalizando os dados
const normalizedData = {
  instanceName: sourceData["Instance Name"],
  instanceUrl: sourceData["Instance Url"],
  instanceToken: sourceData["Instance Token"],
  installationUrl: sourceData["Chatwoot URL"][0].value,
  accountName: sourceData["Chatwoot Account (SET!)"][0].value,
  accountId: sourceData["Chatwoot Account Id"][0].value,
  userName: sourceData["Chatwoot User Name"][0].value,
  userToken: sourceData["User Token"][0].value,
  inboxId: sourceData["Inbox Id üö®"],
  inboxStatus: sourceData["Inbox Status"].value
};

// Retorna os dados normalizados
return normalizedData;

// C√≥digo JavaScript para N8N - Compara√ß√£o de Payloads
const payload = $('Workflow Trigger').item.json.body;

// Valida√ß√£o inicial - verifica se os arrays existem
if (!payload || !payload.items || !payload.old_items) {
  return [{ 
    json: {
      error: "Payload inv√°lido - items ou old_items n√£o encontrados",
      payload_received: payload 
    }
  }];
}

// Verifica se os arrays est√£o vazios
if (payload.items.length === 0 && payload.old_items.length === 0) {
  return [{ 
    json: {
      message: "Nenhum item para comparar - arrays vazios" 
    }
  }];
}

function compareObjects(newObj, oldObj, path = '') {
  const changes = [];

  // Fun√ß√£o auxiliar para comparar valores profundamente
  function deepEqual(a, b) {
    if (a === b) return true;
    if (a == null || b == null) return false;
    if (typeof a !== typeof b) return false;

    if (Array.isArray(a) && Array.isArray(b)) {
      if (a.length !== b.length) return false;
      for (let i = 0; i < a.length; i++) {
        if (!deepEqual(a[i], b[i])) return false;
      }
      return true;
    }

    if (typeof a === 'object' && typeof b === 'object') {
      const keysA = Object.keys(a);
      const keysB = Object.keys(b);
      if (keysA.length !== keysB.length) return false;

      for (let key of keysA) {
        if (!keysB.includes(key) || !deepEqual(a[key], b[key])) {
          return false;
        }
      }
      return true;
    }

    return false;
  }

  // Compara todas as propriedades do objeto novo
  for (const key in newObj) {
    const currentPath = path ? `${path}.${key}` : key;
    const newValue = newObj[key];
    const oldValue = oldObj[key];

    if (!deepEqual(newValue, oldValue)) {
      changes.push({
        property: currentPath,
        old_value: oldValue,
        new_value: newValue
      });
    }
  }

  // Verifica propriedades que existiam no objeto antigo mas n√£o no novo
  for (const key in oldObj) {
    if (!(key in newObj)) {
      const currentPath = path ? `${path}.${key}` : key;
      changes.push({
        property: currentPath,
        old_value: oldObj[key],
        new_value: undefined
      });
    }
  }

  return changes;
}

function findChangesInItems(items, oldItems) {
  const allChanges = [];

  // Compara cada item com seu correspondente em old_items
  for (let i = 0; i < Math.max(items.length, oldItems.length); i++) {
    const newItem = items[i];
    const oldItem = oldItems[i];

    if (!newItem && oldItem) {
      // Item foi removido
      allChanges.push({
        item_index: i,
        item_id: oldItem.id,
        change_type: 'item_removed',
        property: 'entire_item',
        old_value: oldItem,
        new_value: null
      });
    } else if (newItem && !oldItem) {
      // Item foi adicionado
      allChanges.push({
        item_index: i,
        item_id: newItem.id,
        change_type: 'item_added',
        property: 'entire_item',
        old_value: null,
        new_value: newItem
      });
    } else if (newItem && oldItem) {
      // Compara propriedades do item
      const itemChanges = compareObjects(newItem, oldItem);

      itemChanges.forEach(change => {
        allChanges.push({
          change_type: 'property_changed',
          ...change
        });
      });
    }
  }

  return allChanges;
}

// Executa a compara√ß√£o
const changes = findChangesInItems(payload.items, payload.old_items);

// Estrutura a sa√≠da como array de objetos individuais (estilo n8n)
const outputArray = [];

changes.forEach(change => {
  if (change.change_type === 'property_changed') {
    outputArray.push({
      json: {
        property: change.property,
        old: change.old_value,
        new: change.new_value
      }
    });
  }
});

// Retorna array de mudan√ßas - cada uma como item separado
return outputArray.length > 0 ? outputArray : [{ 
  json: { 
    message: "Nenhuma mudan√ßa detectada" 
  } 
}];

// Obt√©m o array de inboxes do payload
const inboxes = $('Obt√©m inboxes').item.json.payload;

// Obt√©m o ID para compara√ß√£o
const targetId = (typeof $('Filter Baserow Change').item.json.new === 'object') 
  ? Number($('Workflow Trigger').item.json.body.items[0]["Inbox Id üö®"]) 
  : Number($('Filter Baserow Change').item.json.new);

// Filtra apenas inboxes do tipo "Channel::Api" e procura pelo ID correspondente
const foundInbox = inboxes
  .filter(inbox => inbox.channel_type === "Channel::Api")
  .find(inbox => inbox.id === targetId);

// Retorna o resultado
if (foundInbox) {
  return {
    found: true,
    id: foundInbox.id,
    name: foundInbox.name,
    channel_id: foundInbox.channel_id
  };
} else {
  return {
    found: false,
    id: null,
    name: null,
    channel_id: null
  };
}

