const apiBody = $('Workflow Trigger').item.json.body;
const switchProcessor = $('Workflow Trigger').item.json.switchProcessor;
const payload = {};

// Fun√ß√£o para processar vCard e extrair informa√ß√µes de contato
function processVCard(vCardString) {
    const vCardData = vCardString.split('\n');
    const contactInfo = {};

    vCardData.forEach((line) => {
        const [key, value] = line.split(':');
        if (key && value) {
            contactInfo[key] = value;
        }
    });

    return contactInfo;
}

// Fun√ß√£o para formatar um contato individual
function formatSingleContact(contactInfo, displayName) {
    const name = displayName || contactInfo['FN'];
    let formattedContact = `üìû *${name}*\n`;

    // Coletar todos os n√∫meros de telefone
    const phoneNumbers = [];
    Object.keys(contactInfo).forEach((key) => {
        if (key.startsWith('item') && key.includes('TEL')) {
            phoneNumbers.push(contactInfo[key]);
        } else if (key.includes('TEL') && !key.startsWith('item')) {
            phoneNumbers.push(contactInfo[key]);
        }
    });

    // Formata√ß√£o diferente para 1 ou m√∫ltiplos n√∫meros
    if (phoneNumbers.length === 1) {
        formattedContact += `‚îî ${phoneNumbers[0]}`;
    } else if (phoneNumbers.length > 1) {
        phoneNumbers.forEach((phone, index) => {
            const isLast = index === phoneNumbers.length - 1;
            const prefix = isLast ? '‚îî' : '‚îú';
            formattedContact += `${prefix} ${phone}`;
            if (!isLast) formattedContact += '\n';
        });
    }

    return formattedContact;
}

/*====================================
COMMON FISRT PROPERTY
====================================*/
payload.eventType = apiBody.EventType;

/*====================================
PROCESSA MESSAGE_UPDATEDS
====================================*/
if (payload.eventType === 'messages_update') {
  payload.type = apiBody.type;

  /*============
  DELETADAS: n√£o tratamos como updated... Convertemos para "messages" seguindo o caminho normal para fazermos a "Cita√ß√£o" de del√ß√£o.
  ============*/
  if (apiBody.type === 'DeletedMessage') { // Poderia ser apiBody.event.Type === 'Deleted', os dois s√£o iguais!
    payload.eventType = 'messages';
    payload.cwMessageType = apiBody.event.IsFromMe ? "outgoing" : "incoming";
    payload.contentType = 'deletion'; //Estou criando esse type para padronizar
    payload.deleted = true; 
    payload.deletedId = apiBody.event.MessageIDs[0]; //Como √© updatedd vem ddentro de IDs no plural, mas vem apenas uma por vez (√ìTIMO!)!
  }

  /*============
  STATUS UPDATE: Todos os eventos que recebemos aqui de update s√£o atualiza√ß√£o de status (se n√£o for o deleted de cima, obviamente)
  ============*/
  // Adiciona o status da mensagem
  if (apiBody.state) { //Todos os eventos de update tem essa propriedade, exceto o event.Type: "retry"! Que eu vou cortar logo no Router.
    switch(apiBody.state) {
      case "Delivered":
        payload.status = "sent";
        break;
      case "Read":
        payload.status = "read";
        break;
      default:
        payload.status = "Status desconhecido";
    }
    payload.messageIds = apiBody.event.MessageIDs?.map(id => `MID:${id}`) || []; //Para todos os IDS adicionamos nosso prefixo
  }

  payload.contactData = {
    chatId: apiBody.event.Chat,
    phoneNumber: apiBody.event.Chat?.split('@')[0] || null
  };


/*====================================
PROCESSA EVENTO "messages" (s√≥ mandamos para c√° esses messages e messages_update, por isso um simples else e n√£o case)
====================================*/
} else { 
  const apiBodyMessage = apiBody.message;
  // N√£o precisamos: if (apiBodyMessage) { ‚Üê Sempre teremos body message!
  payload.wasSentByApi = apiBodyMessage.wasSentByApi;
  payload.cwMessageType = apiBodyMessage.fromMe ? "outgoing" : "incoming";

  //Amtes do bug de normaliza√ß√£o eu usava essa simplificada.
  // BUG: A Uazapi em alguns casos de mensagens de m√≠dia (QR code que enviei pela Evo pelo Typebot) simplesmente mandou a mensagem de m√≠dia com o type "text", mesmo tendo conte√∫do de imagem (mediaType: "image")! Ent√£o t√¥ fazenod agora uma verifica√ß√£o direta dessa propriedade como fallback.
  //let apiBodyMessageType = apiBodyMessage.messageType === 'ExtendedTextMessage' 
    //? 'text' 
    //: apiBodyMessage.type; //text/reaction/media
  //payload.contentType = apiBodyMessageType;

  let apiBodyMessageType;
  if (apiBodyMessage.messageType === 'ExtendedTextMessage' || apiBodyMessage.messageType === 'Conversation') { //Nesse caso a Uazapi PARA ALGUNS payloads n√£o normaliza o apiBodyMessage.type como sendo "text", talvez puramente por bug (como no caso do anuncio de facebook vs instagram), ou por ter cen√°rios complexos, mas... EU vou normalizar como texto normal visto que at√© agora s√≥ lidei com cen√°rios onde, apesar de ser Extended (mensagem de texto normal que vem como Extented por algum motivo, mensagem de an√∫ncio), recebemos normalizado na propriedade text o conte√∫do.
    // 'Conversation' (gerada pea api da Evo!) tamb√©m t√° vindo sem o type como text.
    apiBodyMessageType = 'text';
  } else if (apiBodyMessage.mediaType && apiBodyMessage.mediaType !== '') {
    apiBodyMessageType = 'media';
  } else {
    apiBodyMessageType = apiBodyMessage.type; // text/reaction/media
  }
  payload.contentType = apiBodyMessageType;


  /*============
  TODOS DAS PROPRIEDADES DE TODAS AS MENSAGENS QUE IMPLICAM EM MARCA√á√ÉO (CONSULTA DE stanzaId).
  ============*/
  if (apiBodyMessage.edited) { // Se tem conte√∫do
    payload.edited = !!apiBodyMessage.edited; // Convertemos para booleano
    payload.editedId = apiBodyMessage.edited // Propriedade extra para o valor
  }
  if (apiBodyMessage.reaction) {
    payload.reaction = !!apiBodyMessage.reaction
    payload.reactionId = apiBodyMessage.reaction
  }
  if (apiBodyMessage.quoted) {
    payload.quoted = !!apiBodyMessage.quoted;
    payload.quotedId = apiBodyMessage.quoted; // Est√° vindo normalizado aqui, mas a propriedade "oficial" seria essa: content?.contextInfo?.stanzaID
    payload.contextInfo = apiBodyMessage.content?.contextInfo || {} // Dados "extras" j√° que o que nos inporta √© o quoted id que pegamos acima.
  }

