  }

  /*============
  MESSAGE TYPE AD REPLY (RESPOSTA DE AN√öNCIO)
  ============*/
  // Verifica se a mensagem √© uma resposta a um an√∫ncio pela exist√™ncia do objeto externalAdReply
  if (apiBodyMessage.content?.contextInfo?.externalAdReply) {
