    const separator = '\n\n‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢ ‚Ä¢\n\n';
    payload.text = header + formattedContacts.join(separator);
    payload.contentType = 'text'; // Convertemos para texto para o Chatwoot
  }

  /*============
  MESSAGE TYPE LOCATION / LIVE LOCATION
  ============*/
  if (apiBodyMessage.mediaType === 'location' || apiBodyMessage.mediaType === 'livelocation') {
