// Importa o cliente Redis
const { createClient } = require('redis');

// --- Configura√ß√µes ---
const webhookData = $('Nml - Webhook Data').item.json;
const chatwootConfig = $('Merge - Instance Chatwoot Data').item.json;

const messageIds = webhookData.messageIds;
const newStatus = webhookData.status;
const TTL_SECONDS = 86400;

// Configura√ß√µes de retentativa para a MENSAGEM PRINCIPAL
const RETRY_ATTEMPTS = 20;
const RETRY_DELAYS_MS = {
    image: 500,
    audio: 500,
	ptt: 500,
    document: 1000,
	video: 600,
	default: 250 
};

// NOVIDADE: Configura√ß√µes de retentativa para a busca do TIPO da mensagem 
const TYPE_CHECK_RETRY_ATTEMPTS = 6; // Menos tentativas, pois deve ser mais r√°pido
const TYPE_CHECK_RETRY_DELAY_MS = 350; // Um delay curto e fixo para a busca do tipo

// --- Conex√£o com o Redis ---
const client = createClient({
	socket: {
		host: 'redis',
		port: 6379
	},
	database: 10
});
client.on('error', (err) => console.log('Redis Client Error', err));
await client.connect();

// --- L√≥gica Principal ---
const processedResults = [];
const summaryLines = [];
const typeCheckLog = {}; 
const baseUrlPattern = `${chatwootConfig.installationUrl}/api/v1/accounts/${chatwootConfig.accountId}/conversations/{cvsId}/messages/{msgId}`; //Apenas string para debug.

// Itera sobre o array de messageIds
for (const messageId of messageIds) {
	let cachedMessageRaw = null;
	let cachedMessage = null;
    let messageType = null;

	// ETAPA 1: Buscar o TIPO da mensagem com seu pr√≥prio loop de retentativa
	const typeKey = `${messageId}:TYPE`;
    for (let i = 0; i < TYPE_CHECK_RETRY_ATTEMPTS; i++) {
        messageType = await client.get(typeKey);
        if (messageType) break; // Se encontrou, sai do loop.

        // Se n√£o encontrou, espera um pouco antes da pr√≥xima tentativa
        if (i < TYPE_CHECK_RETRY_ATTEMPTS - 1) {
            await new Promise(resolve => setTimeout(resolve, TYPE_CHECK_RETRY_DELAY_MS));
        }
    }

    // Registra o resultado final da busca pelo tipo (seja ele qual for)
    typeCheckLog[typeKey] = messageType;

	// ETAPA 2: Determinar o delay de retentativa para a mensagem principal
	const retryDelay = RETRY_DELAYS_MS[messageType] || RETRY_DELAYS_MS.default;

	// ETAPA 3: Tentar buscar o objeto principal da mensagem
	for (let i = 0; i < RETRY_ATTEMPTS; i++) {
		cachedMessageRaw = await client.get(messageId);
		if (cachedMessageRaw) break;

		if (i < RETRY_ATTEMPTS - 1) {
			await new Promise(resolve => setTimeout(resolve, retryDelay));
		}
	}

	// ETAPA 4: Prosseguir com o processamento normal da mensagem...
	if (!cachedMessageRaw) {
		const errorMsg = `N√£o encontrado no cache ap√≥s ${RETRY_ATTEMPTS} tentativas (delay: ${retryDelay}ms/tipo: ${messageType || 'n√£o def.'}).`;
		processedResults.push({ messageId, status: 'error', error: errorMsg });
		summaryLines.push(`${messageId}: Falha (Cache)`);
		continue;
	}

	try {
		const cleanedJsonString = cachedMessageRaw.replace(/\\/g, '');
		cachedMessage = JSON.parse(cleanedJsonString);
	} catch (e) {
		const errorMsg = `Falha ao parsear JSON do Redis: ${e.message}`;
		processedResults.push({ messageId, status: 'error', error: errorMsg, rawValue: cachedMessageRaw });
		summaryLines.push(`${messageId}: Falha (JSON)`);
		continue;
	}

	// Valida√ß√µes
	if (!cachedMessage.id || !cachedMessage.cvsId) {
		const errorMsg = "Dados do cache incompletos. 'id' ou 'cvsId' n√£o encontrados.";
		processedResults.push({ messageId, status: 'error', error: errorMsg, cachedMessage });
		summaryLines.push(`${messageId}: Falha (Dados incompletos)`);
		continue;
	}

	if (cachedMessage.status === newStatus) {
		const infoMsg = `Evento "${newStatus}" duplicado. Nenhuma a√ß√£o tomada.`;
		processedResults.push({ messageId, status: 'duplicate', info: infoMsg });
		summaryLines.push(`${messageId}: Duplicado`);
		continue;
	}

	// NOVO: Prote√ß√£o para n√£o atualizar mensagens que j√° est√£o com status "read"
	if (cachedMessage.status === 'read') {
		const infoMsg = `Mensagem j√° est√° com status "read". Atualiza√ß√£o bloqueada para evitar regress√£o.`;
		processedResults.push({ messageId, status: 'blocked', info: infoMsg });
		summaryLines.push(`${messageId}: Bloqueado (read)`);
		continue;
	}

	// ETAPA 5: Atualizar a API do Chatwoot
	const url = `${chatwootConfig.installationUrl}/api/v1/accounts/${chatwootConfig.accountId}/conversations/${cachedMessage.cvsId}/messages/${cachedMessage.id}`;
	try {
		const dataToCache = { ...cachedMessage, status: newStatus };
		await client.set(messageId, JSON.stringify(dataToCache), { EX: TTL_SECONDS });

		const options = {
			method: 'PATCH',
			headers: { 'api_access_token': chatwootConfig.userToken, 'Content-Type': 'application/json' },
			body: { status: newStatus },
			json: true,
			url: url,
			allowUnauthorizedCerts: true,
		};

		const response = await this.helpers.httpRequest(options);
		const infoMsg = `Status atualizado para "${newStatus}" com sucesso.`;
		processedResults.push({ messageId, status: 'success', info: infoMsg, url, apiResponse: response });
		summaryLines.push(`${messageId}: ${newStatus}`);

	} catch (error) {
		const errorMsg = `Falha na API do Chatwoot: ${JSON.stringify(error, null, 2)}`;
		processedResults.push({ messageId, status: 'error', url, error: errorMsg });
		summaryLines.push(`${messageId}: Falha (API)`);
	}
}

// Fecha a conex√£o com o Redis
await client.quit();

// Retorna o objeto final estruturado
return {
	summary: `${messageIds.length} mensagens processadas: ${summaryLines.join(' | ')}`,
	baseUrlPattern: baseUrlPattern,
	typeCheckLog: typeCheckLog,
	results: processedResults
};

